mapper.sql:
		FROM FINGERVEIN a where 1=1


            AND to_char(a.CREATE_TIME, 'YYYY-MM-DD') >= #{param.createDateBegin}


            AND to_char(a.CREATE_TIME, 'YYYY-MM-DD') <= #{param.createDateEnd}

mapper.select:
SELECT a.* FROM FINGERVEIN a
    where a.STATUS = #{param.status, jdbcType=VARCHAR}
	order by a.CREATE_TIME desc

