mapper.sql:
		FROM ROLE a where 1=1


            AND to_char(a.CREATE_TIME, 'YYYY-MM-DD') >= #{param.createDateBegin}


            AND to_char(a.CREATE_TIME, 'YYYY-MM-DD') <= #{param.createDateEnd}

mapper.update:
UPDATE ROLE a


 a.NAME = #{param.name, jdbcType=VARCHAR},

a.UPDATE_TIME = #{param.updateTime, jdbcType=TIMESTAMP},
a.UPDATE_BY = #{param.updateBy, jdbcType=VARCHAR}

WHERE a.ID = #{param.id, jdbcType=VARCHAR}

mapper.delete:
delete from ROLE a where a.ID in (

  #{id, jdbcType=VARCHAR}
)
