mapper.sql:
		FROM RECOGNITION_LOG a where 1=1


            AND a.NAME = #{param.name}


            AND a.IPADDR = #{param.ipaddr}


            AND a.STATUS = #{param.status}


            AND date_format(a.CREATE_TIME, '%Y-%m-%d') >= #{param.createDateBegin}


            AND date_format(a.CREATE_TIME, '%Y-%m-%d') <= #{param.createDateEnd}

        AND a.TYPE = '1'

mapper.select:
SELECT a.IPADDR, count(case when a.TYPE = '1' and a.STATUS = '1' then 1 end) REC_SUCCTOTAL, count(case when a.TYPE = '1' and a.STATUS = '0' then 1 end) REC_FAILTOTAL, count(case when a.TYPE = '2' and a.STATUS = '1' then 1 end) PWD_SUCCTOTAL, count(case when a.TYPE = '2' and a.STATUS = '0' then 1 end) PWD_FAILTOTAL from RECOGNITION_LOG a where 1 = 1
AND a.IPADDR = #{param.ipaddr}
AND a.STATUS = #{param.status}
AND date_format(a.CREATE_TIME, '%Y-%m-%d') >= #{param.createDateBegin}
AND date_format(a.CREATE_TIME, '%Y-%m-%d') <= #{param.createDateEnd}
group by a.IPADDR

mapper.delete:
delete from RECOGNITION_LOG a where a.ID in (

  #{id, jdbcType=VARCHAR}
)
